import React from "react";
import "./posts.scss";
import Container from "../layout/Container/Container";
import PostItem from "./post-item/PostItem";
import { useSelector, useDispatch } from "react-redux";
import { ThreeDots } from "react-loader-spinner";
import getPost from "../../redux/slices/post.slice"

const LatestProducts = () => {
  const dispatch = useDispatch();

  const { isLoading, posts } = useSelector(
    (state) => state.posts
  );

  console.log(isLoading);

  React.useEffect(() => {

    dispatch(getPost());
  }, [dispatch]);

  return (
    <Container>
      <div className="latest-products-container left column">
        <div className="latest-product-sub sub-heading">Latest Products</div>
        {isLoading ? (
          <div className="spinner center">
            <ThreeDots
              height="80"
              width="80"
              radius="9"
              color="#003F62"
              ariaLabel="three-dots-loading"
              wrapperStyle={{}}
              wrapperClassName=""
              visible={true}
            />
          </div>
        ) : (
          <div className="products-container space-between wrap">
            {posts &&
              posts.map((product) => (
                <div className="product-item" key={product?._id}>
                  <PostItem data={product} />
                </div>
              ))}
          </div>
        )}
      </div>
    </Container>
  );
};

export default LatestProducts;
