import React, { useState, useEffect } from "react";
import DashboardPanel from "../dashboard/DashboardPanel";
import "./category.scss";

function Category() {
  const [categories, setCategories] = useState([]);
  const [title, setTitle] = useState("");
  const [created, setCreated] = useState("");
  const [currentCategoryId, setCurrentCategoryId] = useState("");
  const [buttonText, setButtonText] = useState("Create");

  const apiUrl = "https://example.com/categories";

  useEffect(() => {
    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => setCategories(data));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();

    if (buttonText === "Create") {
      // Create a new category
      fetch(apiUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ title }),
      })
        .then((response) => response.json())
        .then((data) => {
          setCategories([...categories, data]);
          setTitle("");
        })
        .catch((error) => console.error(error));
    } else {
      // Update an existing category
      fetch(`${apiUrl}/${currentCategoryId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ title, created }),
      })
        .then((response) => response.json())
        .then((data) => {
          setCategories(
            categories.map((category) =>
              category.id === data.id ? data : category
            )
          );
          setTitle("");
          setCreated("");
          setButtonText("Create");
        })
        .catch((error) => console.error(error));
    }
  };

  const handleEdit = (categoryId) => {
    const category = categories.find((category) => category.id === categoryId);
    setTitle(category.title);
    setCreated(category.created);
    setCurrentCategoryId(category.id);
    setButtonText("Edit");
  };

  const handleDelete = (categoryId) => {
    fetch(`${apiUrl}/${categoryId}`, {
      method: "DELETE",
    })
      .then(() => {
        setCategories(
          categories.filter((category) => category.id !== categoryId)
        );
      })
      .catch((error) => console.error(error));
  };

  return (
    <div className="Category">
      <h1>Categories</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          className="Category__input"
        />
        <button type="submit" className="Category__button">
          {buttonText}
        </button>
      </form>
      <table className="Category__table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Created</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.id}>
              <td>{category.title}</td>
              <td>{category.created}</td>
              <td>
                <button
                  onClick={() => handleEdit(category.id)}
                  className="Category__editButton"
                >
                  Edit
                </button>
              </td>
              <td>
                <button
                  onClick={() => handleDelete(category.id)}
                  className="Category__deleteButton"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Category;
