import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { toast } from "react-toastify";
import { PostService } from "../../services";
import { formatErrorResponse } from "../../utils";

export const getPostsAction = createAsyncThunk("post/all", async (thunkAPI) => {
  try {
    const data = await PostService.getAllPosts();
    return data.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const createPostAction = createAsyncThunk(
  "post/create",
  async ({ title, content, image }, thunkAPI) => {
    try {
      const data = await PostService.createPost(title, content, image);
      if (data?.data?.token) {
        localStorage.setItem("token", JSON.stringify(data.data.token));
      }
      toast.success("user created successfully");
      console.log(data, "reg");
      return data;
    } catch (error) {
      const message = formatErrorResponse(error);
      console.log(error, "error");
      toast.error(message);
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const updateCategoryAction = createAsyncThunk("category/update", async ({title, id}, thunkAPI) => {
  try {
    const response = await CategoryService.updateCategory(title, id);
    toast.success("edited successfully");
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const deleteCategoryAction = createAsyncThunk("category/delete", async (id, thunkAPI) => {
  try {
    const response = await CategoryService.deleteCategory(id);
    toast.success("deleted successfully");
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});


const initialState = {
  isLoading: true,
  postsData: [],
};

const postSlice = createSlice({
  name: "post",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(getPostsAction.pending, (state) => {
      state.isLoading = true;
      state.postsData = []
    });
    builder.addCase(getPostsAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.postsData = action.payload;
    });
    builder.addCase(getPostsAction.rejected, (state, action) => {
      state.isLoading = false;
    });
  },
});

const { reducer } = postSlice;
export default reducer;
