import React, { useState } from "react";
import "./customselect.scss";

const CustomSelect = () => {
  const [selectedTags, setSelectedTags] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  const handleTagSelect = (event) => {
    const selectedTagId = event.target.value;
    if (selectedTags.includes(selectedTagId)) {
      setSelectedTags(selectedTags.filter((id) => id !== selectedTagId));
    } else {
      setSelectedTags([...selectedTags, selectedTagId]);
    }
  };

  const handleCategorySelect = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleRemoveTag = (tagId) => {
    setSelectedTags(selectedTags.filter((id) => id !== tagId));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const tagIds = selectedTags.reduce(
      (acc, id, index) =>
        acc +
        (index === 0 ? "?tagIds[]=" : "&tagIds[]=") +
        encodeURIComponent(id),
      ""
    );

    const categoryId = selectedCategory
      ? `&categoryId=${encodeURIComponent(selectedCategory)}`
      : "";

    // Send the data to your Express server
    fetch(`/api/posts${tagIds}${categoryId}`, {
      method: "POST",
      body: JSON.stringify({ /* your post data */ }),
      headers: {
        "Content-Type": "application/json"
      }
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the response from your server
      })
      .catch((error) => {
        // Handle the error
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="tag-select">
        <label htmlFor="tags">Select tags:</label>
        <select
          id="tags"
          name="tags"
          multiple
          onChange={handleTagSelect}
          value={selectedTags}
        >
          <option value="tag1">Tag 1</option>
          <option value="tag2">Tag 2</option>
          <option value="tag3">Tag 3</option>
        </select>
        <div className="tag-chips">
          {selectedTags.map((tagId) => (
            <div className="tag-chip" key={tagId}>
              <span>{tagId}</span>
              <button
                type="button"
                onClick={() => handleRemoveTag(tagId)}
              >
                &times;
              </button>
            </div>
          ))}
        </div>
      </div>
      <div className="category-select">
        <label htmlFor="category">Select category:</label>
        <select
          id="category"
          name="category"
          onChange={handleCategorySelect}
          value={selectedCategory}
        >
          <option value="">Select a category</option>
          <option value="category1">Category 1</option>
          <option value="category2">Category 2</option>
          <option value="category3">Category 3</option>
        </select>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default CustomSelect;
