import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { toast } from "react-toastify";
import { TagService } from "../../services";
import { formatErrorResponse } from "../../utils";

export const getTagsAction = createAsyncThunk("tag/all", async (thunkAPI) => {
  try {
    const response = await TagService.getAllTags();
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const createTagAction = createAsyncThunk("tag/create", async (title, thunkAPI) => {
  try {
    const response = await TagService.createTag(title);
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const updateTagAction = createAsyncThunk("tag/update", async ({title, id}, thunkAPI) => {
  try {
    const response = await TagService.updateTag(title, id);
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const deleteTagAction = createAsyncThunk("tag/delete", async (id, thunkAPI) => {
  try {
    const response = await TagService.deleteTag(id);
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

const initialState = {
  isLoading: true,
  tagsData: [],
};

const tagSlice = createSlice({
  name: "tag",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    // get tags cases
    builder.addCase(getTagsAction.pending, (state) => {
      state.isLoading = true;
      state.tagsData = []
    });
    builder.addCase(getTagsAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.tagsData = action.payload;
    });
    builder.addCase(getTagsAction.rejected, (state, action) => {
      state.isLoading = false;
    });

    // create tag cases
    builder.addCase(createTagAction.pending, (state) => {
      state.isLoading = true;
      state.tagsData = []
    });
    builder.addCase(createTagAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.tagsData = action.payload;
    });
    builder.addCase(createTagAction.rejected, (state, action) => {
      state.isLoading = false;
    });

    // update tags cases
    builder.addCase(updateTagAction.pending, (state) => {
      state.isLoading = true;
    });
    builder.addCase(updateTagAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.tagsData = action.payload;
    });
    builder.addCase(updateTagAction.rejected, (state, action) => {
      state.isLoading = false;
    });

    // delete tag cases
    builder.addCase(deleteTagAction.pending, (state) => {
      state.isLoading = true;
    });
    builder.addCase(deleteTagAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.tagsData = [];
    });
    builder.addCase(deleteTagAction.rejected, (state, action) => {
      state.isLoading = false;
    });
  },
});

const { reducer } = tagSlice;
export default reducer;
