import "../dashboard.scss";
import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  deleteTagAction,
  getTagsAction,
  updateTagAction,
  createTagAction,
} from "../../../redux/slices/tag.slice";
import { ThreeDots } from "react-loader-spinner";
import DashboardPanel from "../DashboardPanel";
import Input from "../../primitives/Input/Input";
import Button from "../../primitives/Button/Button";

function Tags() {
  const [title, setTitle] = useState("");
  const [currentCategoryId, setCurrentCategoryId] = useState("");
  const [buttonText, setButtonText] = useState("Create");

  const dispatch = useDispatch();
  const { isLoading, tagsData } = useSelector((state) => state.tag);
  console.log(tagsData, "Tags");

  const handleSubmit = (event) => {
    event.preventDefault();

    if (buttonText === "Create") {
      // Create a new category
      dispatch(createTagAction(title));
      setButtonText("Create");
    } else {
      // Update an existing category
      dispatch(updateTagAction({ title: title, id: currentCategoryId }));
    }
  };
  const handleEdit = (tagId) => {
    const tag = tagsData.find(
      (tag) => tag?._id === tagId
    );
    setTitle(tag?.title);
    setCurrentCategoryId(tag?._id);
    setButtonText("Edit");
  };

  const handleDelete = (tagId) => {
    dispatch(deleteTagAction(tagId));
  };

  useEffect(() => {
    dispatch(getTagsAction());
  }, [dispatch]);

  return (
    <div className="center">
      <DashboardPanel />
      <div className="category">
        <h1>Tags</h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title:</label>
          <Input
            type="text"
            id="title"
            name="title"
            value={title}
            onChange={(event) => setTitle(event.target.value)}
            className="Category__input"
          />
          <Button
            type="submit"
            isLoading={isLoading}
            className="Category__button"
          >
            {buttonText}
          </Button>
        </form>
        <table className="Category__table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Created</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          {isLoading ? (
            <div
              className="center"
              style={{ height: "200px", width: "100%", background: "red" }}
            >
              <ThreeDots
                height="80"
                width="80"
                radius="9"
                color="#003F62"
                ariaLabel="three-dots-loading"
                wrapperStyle={{}}
                wrapperClassName=""
                visible={true}
              />
            </div>
          ) : (
            <tbody>
              {tagsData &&
                tagsData?.map((category) => (
                  <tr key={category._id}>
                    <td>{category?.title}</td>
                    <td>{category?.createdAt}</td>
                    <td>
                      <button
                        onClick={() => handleEdit(category._id)}
                        className="Category__editButton"
                      >
                        Edit
                      </button>
                    </td>
                    <td>
                      <button
                        onClick={() => handleDelete(category._id)}
                        className="Category__deleteButton"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
            </tbody>
          )}
        </table>
        <div>{!categoriesData && "No tags created"}</div>
      </div>
    </div>
  );
}

export default Tags;
