import React, { useState } from "react";
import "./imageinput.scss";

const ImageUploadInput = ({ setImageFile }) => {
  const [image, setImage] = useState(null);

  const reader = new FileReader();

  const handleImageUpload = (event) => {
    const file = event.target.files[0];

    reader.onloadend = () => {
      setImage(reader.result);
      setImageFile(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    reader.result = null;
    setImage(null);
    setImageFile(null);
  };

  return (
    <div className="image-upload-input-container">
      {image ? (
        <img src={image} alt="uploaded" className="uploaded-image" />
      ) : (
        <div className="default" >no file chosen</div>
      )}
      <input
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
        className="image-input"
      />
      <label htmlFor="image-input" className="image-label">
        Upload Image
      </label>
      {image && (
        <div onClick={removeImage} className="image-label">
          remove Image
        </div>
      )}
    </div>
  );
};

export default ImageUploadInput;
