import "./category.scss";
import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteTagAction, getTagsAction, updateTagAction, createTagAction } from "../../../redux/slices/tag.slice";
import DashboardPanel from "../DashboardPanel";
import Input from "../../primitives/Input/Input";
import Button from "../../primitives/Button/Button";

function Category() {
  const [title, setTitle] = useState("");
  const [currentCategoryId, setCurrentCategoryId] = useState("");
  const [buttonText, setButtonText] = useState("Create");

  const dispatch = useDispatch()
  const {isLoading, tagsData} = useSelector(state => state.tag)

  useEffect(() => {
    dispatch(getTagsAction);
  }, [dispatch]);

  const handleSubmit = (event) => {
    event.preventDefault();

    if (buttonText === "Create") {
      // Create a new category
      dispatch(createTagAction(title))
      setButtonText("Create");
    } else {
      // Update an existing category
      dispatch(updateTagAction(title, currentCategoryId))
    }
  };

  const handleEdit = (categoryId) => {
    const category = categories.find((category) => category.id === categoryId);
    setTitle(category.title);
    setCurrentCategoryId(category.id);
    setButtonText("Edit");
  };

  const handleDelete = (categoryId) => {
    dispatch(deleteTagAction(categoryId));
  };

  return (
    <div className="center">
      <DashboardPanel />
      <div className="category">
        <h1>Categories</h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Title:</label>
          <Input
            type="text"
            id="title"
            name="title"
            value={title}
            onChange={(event) => setTitle(event.target.value)}
            className="Category__input"
          />
          <Button type="submit" className="Category__button">
            {buttonText}
          </Button>
        </form>
        <table className="Category__table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Created</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {tagsData && tagsData?.map((category) => (
              <tr key={category.id}>
                <td>{category.title}</td>
                <td>{category.created}</td>
                <td>
                  <button
                    onClick={() => handleEdit(category.id)}
                    className="Category__editButton"
                  >
                    Edit
                  </button>
                </td>
                <td>
                  <button
                    onClick={() => handleDelete(category.id)}
                    className="Category__deleteButton"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Category;
