import React from "react";
import "./button.scss";
import { ThreeDots } from "react-loader-spinner";

const Button = ({ isLoading, children, ...rest }) => {
  return (
    <button className={`button ${isLoading ? "loading" : ""}`} {...rest}>
      {isLoading ? (
        <ThreeDots
          height="10"
          width="10"
          radius="9"
          color="#003F62"
          ariaLabel="three-dots-loading"
          wrapperStyle={{}}
          wrapperClassName=""
          visible={true}
        />
      ) : (
        children
      )}
    </button>
  );
};

export default Button;
