import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { toast } from "react-toastify";
import { CategoryService } from "../../services";
import { formatErrorResponse } from "../../utils";

export const getCategoriesAction = createAsyncThunk("posts", async (thunkAPI) => {
  try {
    const response = await CategoryService.getAllCategories();
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const createCategoryAction = createAsyncThunk("posts", async (title, thunkAPI) => {
  try {
    const response = await CategoryService.createCategory(title);
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const updateCategoryAction = createAsyncThunk("posts", async (thunkAPI) => {
  try {
    const response = await CategoryService.getAllCategories();
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

export const deleteCategory = createAsyncThunk("posts", async (thunkAPI) => {
  try {
    const response = await CategoryService.getAllCategories();
    return response.data;
  } catch (error) {
    const message = formatErrorResponse(error);
    toast.error(message);
    return thunkAPI.rejectWithValue(message);
  }
});

const initialState = {
  isLoading: true,
  categoriesData: [],
};

const categorySlice = createSlice({
  name: "category",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    // get all categories cases
    builder.addCase(getCategoriesAction.pending, (state) => {
      state.isLoading = true;
      state.postsData = []
    });
    builder.addCase(getCategoriesAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.postsData = action.payload;
    });
    builder.addCase(getCategoriesAction.rejected, (state, action) => {
      state.isLoading = false;
    });

    // create category cases
    builder.addCase(getCategoriesAction.pending, (state) => {
      state.isLoading = true;
      state.postsData = []
    });
    builder.addCase(getCategoriesAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.postsData = action.payload;
    });
    builder.addCase(getCategoriesAction.rejected, (state, action) => {
      state.isLoading = false;
    });

    // update category cases
    builder.addCase(getCategoriesAction.pending, (state) => {
      state.isLoading = true;
      state.postsData = []
    });
    builder.addCase(getCategoriesAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.postsData = action.payload;
    });
    builder.addCase(getCategoriesAction.rejected, (state, action) => {
      state.isLoading = false;
    });

    // delete category cases
    builder.addCase(getCategoriesAction.pending, (state) => {
      state.isLoading = true;
      state.postsData = []
    });
    builder.addCase(getCategoriesAction.fulfilled, (state, action) => {
      state.isLoading = false;
      state.postsData = action.payload;
    });
    builder.addCase(getCategoriesAction.rejected, (state, action) => {
      state.isLoading = false;
    });
  },
});

const { reducer } = categorySlice;
export default reducer;
