import React from "react";
import "./search.scss";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { searchPostAction } from "../../../redux/slices/post.slice";
import Button from "../../primitives/Button/Button";
import Input from "../../primitives/Input/Input";

const SearchBar = () => {
  const navigate = useNavigate();

  const dispatch = useDispatch();

  const { isLoading, postsData } = useSelector((state) => state.post);
  const posts = postsData?.data;
  console.log(posts);

  React.useEffect(() => {
    dispatch(searchPostAction());
  }, [dispatch]);

  const [keyword, setKeyword] = React.useState("");

  console.log(keyword);

  const searchHandler = (e) => {
    e.preventDefault();
    if (keyword) {
      dispatch(searchPostAction());
    } else {
      navigate("/");
    }
  };

  return (
    <div className="searchBar-wrap">
      <form onSubmit={searchHandler}>
        <Input
          type="text"
          placeholder="Search"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        {keyword && <div style={{marginTop: "5px"}} onClick={setKeyword("")}>X</div>}
        <Button isLoading={isLoading}>Search</Button>
      </form>
    </div>
  );
};

export default SearchBar;
